$installer = $this;

$installer->startSetup();

try {
    $installer->getConnection()
        ->addColumn($installer->getTable('oggetto_faq/table_questions'),
            'with_feedback', Varien_Db_Ddl_Table::TYPE_BOOLEAN, null, [
                'nullable' => true,
                'default'  => '0',
            ], 'With Feedback');

} catch (Exception $e) {

    Mage::logException($e);

}

$installer->endSetup();
